import React, { useState } from 'react'
import {useTimer} from '../hooks/useTimer'

export const TimerApp = () => {

    let timerId = 0;

    const [state, setState] = useState({
        counter: 0,
        timerOn: false
    });
    

    const handleStart = () => {
        setState({
            counter: state.counter,
            timerOn: true
        });
    }

    const handleStop = () => {
        setState({
            counter: state.counter,
            timerOn: false
        });
    }

    const handleReset = () => {
        setState({
            counter: 0,
            timerOn: false
        });
    }
    
    const {hours, minutes, seconds} = useTimer(state, setState);
    
    return (
        <>
            <div>
                <div className="card">
                    <div className="card-header text-center">
                        <h2>{"SNOW timer"}</h2>
                    </div>
                    <div className="card-body container">
                        <div>
                          <h2 className="text-center">{`${hours.toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false})}:${minutes.toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false})}:${seconds.toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false})}`}</h2>
                        </div>
                        <div>
                          <button type="button" className="btn btn-success" onClick={handleStart}>Start</button>
                          <button type="button" className="btn btn-danger" onClick={handleStop}>Stop</button>
                          <button type="button" className="btn btn-warning" onClick={handleReset}>Reset</button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
