import React, { useState } from 'react'
import { useBMI } from '../hooks/useBMI';

export const BMICalculatorApp = () => {

    const [inputHeight, setinputHeight] = useState(0)

    const [inputWeight, setinputWeight] = useState(0)

    const [bmi, status] = useBMI(0, 0);

    const handleInputChange = (weight, height) => {

    }

    return (
        <>
            <div className="card">
                <div className="card-header">
                    <h2>{"BMI healthy weight Calculator"}</h2>
                </div>
                <div className="card-body">
                    <p>{`Body Mass Index (BMI) = ${bmi}`}</p>
                    <p>{status}</p>
                    <form>
                        <input 
                            type = "text"
                            value = {inputHeight}
                            onChange = { height => handleInputChange(height, inputWeight)}
                        />
                        <input 
                            type = "text"
                            value = {inputWeight}
                            onChange = { weight => handleInputChange(inputHeight, weight)}
                        />
                    </form>
                </div>
            </div>
        </>
    )
}
