import { useEffect, useState } from "react"
import {PropTypes} from 'prop-types';
import { calculateBMI } from "../helpers/calculateBMI";


export const useBMI = ({height, weight}) => {
    const [state, setState] = useState({
        bmi: 0,
        status: "Normal weight"
    });

    useEffect(() => {
        calculateBMI(height,weight).then( ({bmi, status}) =>{
            setState({
                bmi: bmi,
                status: status
            });
        } )
    }, [height, weight])

    return state;
}

useBMI.propTypes = {
    height: PropTypes
}
