import React, { useState } from 'react'
import { useBMI } from '../hooks/useBMI';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
  } from 'reactstrap';

export const BMICalculatorApp = () => {

    const [inputs, setInputs] = useState({
        inputHeight: 0,
        inputWeight: 0,
    });

    const handleHeightChange = (e) => {
        setInputs({
            inputHeight: e.target.value,
            inputWeight: inputs.inputWeight
        });
    }

    const handleWeightChange = (e) => {
        setInputs({
            inputHeight: inputs.inputHeight,
            inputWeight: e.target.value
        });
    }

    const handleReset = () =>{
        setInputs({
            inputHeight: 0,
            inputWeight: 0
        })
    }

    const {bmi, status} = useBMI(inputs);

    return (
        <>
            <div className="card">
                <div className="card-header">
                    <h2>{"BMI healthy weight Calculator"}</h2>
                </div>
                <Card>
                    <CardBody>
                        <p>{`Body Mass Index (BMI) = ${bmi}`}</p>
                        <p>{status}</p>
                        <form>
                            <label for="Height">{"Height(cm)"}</label>
                            <input 
                                id = "Height"
                                type = "number"
                                value = {inputs.inputHeight}
                                onChange = { handleHeightChange }
                            />
                            <label for="Weight">{"Weight(kg)"}</label>
                            <input
                                id = "Weight" 
                                type = "number"
                                value = {inputs.inputWeight}
                                onChange = { handleWeightChange }
                            />
                        </form>
                        <button onClick={handleReset}>Reset</button>
                    </CardBody>
                </Card>
            </div>
        </>
    )
}
