import React, { useState } from 'react'
import { useBMI } from '../hooks/useBMI';

export const BMICalculatorApp = () => {

    const [inputs, setInputs] = useState({
        inputHeight: 0,
        inputWeight: 0,
    });

    const handleHeightChange = (e) => {
        setInputs({
            inputHeight: e.target.value,
            inputWeight: inputs.inputWeight
        });
    }

    const handleWeightChange = (e) => {
        setInputs({
            inputHeight: inputs.inputHeight,
            inputWeight: e.target.value
        });
    }

    const handleReset = () =>{
        setInputs({
            inputHeight: 0,
            inputWeight: 0
        })
    }

    const {bmi, status} = useBMI(inputs);

    return (
        <>
            <div className="card">
                <div className="card-header text-center">
                    <h2>{"BMI healthy weight Calculator"}</h2>
                </div>
                <div className="card-body container">
                    <div className="text-center">
                        <h4>{`Body Mass Index (BMI) = ${bmi}`}</h4>
                        <h4>{status}</h4>
                    </div>
                    <div>
                        <form>
                            <div>
                                <label htmlFor="Height">{"Height(cm)"}</label>
                                <input 
                                    id = "Height"
                                    type = "number"
                                    value = {inputs.inputHeight}
                                    onChange = { handleHeightChange }
                                />
                            </div>
                            <div>
                                <label htmlFor="Weight">{"Weight(kg)"}</label>
                                <input
                                    id = "Weight" 
                                    type = "number"
                                    value = {inputs.inputWeight}
                                    onChange = { handleWeightChange }
                                />
                            </div>
                        </form>
                    </div>
                    
                    <div>
                        <button onClick={handleReset}>Reset</button>
                    </div>
                </div>
            </div>
        </>
    )
}
