import React, { useState } from 'react'

export const TimerApp = () => {    
    
    // const [hour, ] = new Date().toLocaleTimeString("en-US").split(" ");
    // const [hours, minutes, seconds] = hour.split(":");
    // console.log(hours, minutes, seconds);

    const [hour, setHour] = useState({
        hours: 0,
        minutes: 0,
        seconds: 0,
        running: false 
    });

    const {hours, minutes, seconds, running} = hour;

    const handleStart = () => {
        while(running)
        setHour({
            hours: hours,
            minutes: minutes,
            seconds: seconds+1
        })
    }

    const handleStop = () => {
        console.log("Stop");
    }

    const handleReset = () => {
        console.log("Reset");
    }
    
    return (
        <>
            <div>
                <div className="card">
                    <div className="card-header text-center">
                        <h2>{"SNOW timer"}</h2>
                    </div>
                    <div className="card-body">
                        <h2><time dateTime={`00:00:00`}>{`${hours}:${minutes}:${seconds}`}</time></h2>
                        <button onClick={handleStart}>Start</button>
                        <button onClick={handleStop}>Stop</button>
                        <button onClick={handleReset}>Reset</button>
                    </div>
                </div>
            </div>
        </>
    )
}
