import { useEffect, useState } from 'react';

export const useTimer = (state) => {
    const [hour, setHour] = useState({
        hours: 0,
        minutes: 0,
        seconds: 0
    });

    useEffect(() => {


        calculateTime(props.counter)
            .then( time =>{
                if(state.timerOn){
                    setHour({
                        hours: time.hours,
                        minutes: time.minutes,
                        seconds: time.seconds
                    });
                }
            } )
        }, [props])

    return hour;
}
