export const calculateBMI = async(height, weight) => {
    const bmi = (weight*1000/((height*height)/100));
    console.log(bmi);
    let status = '';
    if(bmi > 18.5 && bmi < 25){
        status = "Normal Weight";
    } else if(bmi >= 25){
        status = "Overweight";
    } else if(bmi < 18.5 && bmi > 0){
        status = "Underweight";
    } else{
        status = "invalid height or weight"
    }
    return {bmi, status};
}
