import React, { useState } from 'react'
import { useBMI } from '../hooks/useBMI';
import InputField from './InputField';

export const BMICalculatorApp = () => {

    const [inputHeight, setinputHeight] = useState(0)

    const [inputWeight, setinputWeight] = useState(0)

    const {bmi, status} = useBMI(inputHeight, inputHeight);
    
    //console.log(bmi, status);

    return (
        <>
            <div className="card">
                <div className="card-header">
                    <h2>{"BMI healthy weight Calculator"}</h2>
                </div>
                <div className="card-body">
                    <p>{`Body Mass Index (BMI) = ${bmi}`}</p>
                    <p>{status}</p>
                    <div>
                        <InputField 
                            state={inputHeight} 
                            setState={setinputHeight}
                        />
                        <InputField 
                            state={inputWeight} 
                            setState={setinputWeight}
                        />
                        {/* <input 
                            type = "number"
                            value = {inputHeight}
                            onChange = { handleHeightChange }
                        />
                        <input 
                            type = "number"
                            value = {inputWeight}
                            onChange = { handleWeightChange }
                        /> */}
                    </div>
                </div>
            </div>
        </>
    )
}
