import React, { useState } from 'react'

export const TimerApp = () => {

    let timerId = 0;

    const [state, setState] = useState({
        // time: {hours: 0,
        // minutes: 0,
        // seconds: 0},
        counter: 0,
        timerOn: false
    });
    

    const handleStart = () => {
        setState({
            counter: state.counter,
            timerOn: true
        })
    }

    const handleStop = () => {
        setState({
            counter:counter,
            timerOn: false
        })
    }

    const handleReset = () => {
        setState({
            counter: 0,
            timerOn: false
        })
    }

    const {hours, minutes, seconds} = useTimer(state);
    
    return (
        <>
            <div>
                <div className="card">
                    <div className="card-header text-center">
                        <h2>{"SNOW timer"}</h2>
                    </div>
                    <div className="card-body">
                        <h2>{`${hours.toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false})}:${minutes.toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false})}:${seconds.toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false})}`}</h2>
                        <button onClick={handleStart}>Start</button>
                        <button onClick={handleStop}>Stop</button>
                        <button onClick={handleReset}>Reset</button>
                    </div>
                </div>
            </div>
        </>
    )
}
