import { useEffect, useState } from 'react';
import { calculateBMI } from '../helpers/calculateBMI';


export const useBMI = (height, weight) => {
    const [state, setState] = useState({
        bmi: 0,
        status: "Normal weight"
    });

    useEffect(() => {

        calculateBMI(height,weight)
            .then( state =>{
                console.log(state);
                setState({
                    bmi: state.bmi,
                    status: state.status
                });

            } )
        }, [weight, height])

    return state;
}
