import React, { useState, useEffect } from 'react'

export const TimerApp = () => {

    let timerId = 0;

    const [hour, setHour] = useState({
        time: {hours: 0,
        minutes: 0,
        seconds: 0},
        counter: 1,
        timerOn: false
    });
    
    const {hours, minutes, seconds} = hour.time;

    const handleStart = () => {
        setHour({
            time: {hours: parseInt(minutes/60),
            minutes: parseInt(hour.counter/60)%60, 
            seconds: (hour.counter)%60},
            counter: hour.counter,
            timerOn: true
        })
    }

    const handleStop = () => {
        setHour({
            time: {hours: parseInt(minutes/60),
            minutes: parseInt(hour.counter/60)%60, 
            seconds: (hour.counter)%60},
            counter: hour.counter,
            timerOn: false
        })
        clearInterval(timerId);
    }

    const handleReset = () => {
        setHour({
            time: {hours: 0,
            minutes: 0,
            seconds: 0},
            counter: 1,
            timerOn: true
        })
    }
    
    return (
        <>
            <div>
                <div className="card">
                    <div className="card-header text-center">
                        <h2>{"SNOW timer"}</h2>
                    </div>
                    <div className="card-body">
                        <h2>{`${hours.toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false})}:${minutes.toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false})}:${seconds.toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false})}`}</h2>
                        <button onClick={handleStart}>Start</button>
                        <button onClick={handleStop}>Stop</button>
                        <button onClick={handleReset}>Reset</button>
                    </div>
                </div>
            </div>
        </>
    )
}
